mapper.select:
SELECT 
	count(*) as cnt 
FROM MEMBER 
WHERE 1 = 1
	AND ID = #{id} 
	AND PASSWORD = #{password}

mapper.update:
UPDATE 
	MEMBER
		SET PASSWORD = #{password_after}
	WHERE 1 = 1
		AND ID = #{id}
		AND PASSWORD = #{password}

mapper.delete:
DELETE
FROM MEMBER
WHERE 1 = 1
AND ID = #{id}
AND PASSWORD = #{password}
